mapper.insert:
INSERT INTO cinema_tbl (
    name
    , genre_id
    , play_time
    , casts
    , description
    , restrict_age
) VALUES (
    #{name}
    , #{genreId}
    , #{playTime}
    , #{casts}
    , #{description}
    , #{restrictAge}
)

mapper.update:
UPDATE cinema_tbl SET
    name = #{name}
    , genre_id      = #{genreId}
    , play_time     = #{playTime}
    , casts         = #{casts}
    , description   = #{description}
    , restrict_age  = #{restrictAge}
WHERE id = #{id}
