mapper.insert:
INSERT INTO coffee_tbl (
name
, price
, source
, shots
) VALUES (
#{name}
, #{price}
, #{source}
, #{shots}
)

mapper.update:
UPDATE coffee_tbl SET
    name = #{name}
    , price = #{price}
    , source = #{source}
    , shots = #{shots}
WHERE id = #{id}

mapper.select:
SELECT id , name , price , source , shots FROM coffee_tbl
WHERE name LIKE CONCAT('%', #{search}, '%')
ORDER BY id LIMIT #{row} OFFSET #{offset}
