mapper.insert:
INSERT INTO food_tbl (
    name
    , salty_level
    , sour_level
    , spicy_level
    , sweet_level
    , food_category_id
) VALUES (
    #{name}
    , #{saltyLevel}
    , #{sourLevel}
    , #{spicyLevel}
    , #{sweetLevel}
    , #{foodCategoryId}
)

mapper.update:
UPDATE food_tbl SET
    name = #{name}
    , name = #{saltyLevel}
    , salty_level = #{sourLevel}
    , sour_level = #{spicyLevel}
    , spicy_level = #{sweetLevel}
    , sweet_level = #{foodCategoryId}
    , food_category_id = #{ingredientId}
WHERE ID = #{id}

mapper.select:
SELECT ft.id , ft.name , ft.salty_level , ft.sour_level , ft.spicy_level , ft.sweet_level , ft.food_category_id , fct.name AS food_category_name FROM food_tbl AS ft LEFT JOIN food_category_tbl AS fct ON ft.food_category_id = fct.id
1=1
AND ft.name LIKE CONCAT('%', #{name}, '%')
AND ft.food_category_id = #{foodCategoryId}
ORDER BY
${item.property} ${item.direction}
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}
