mapper.insert:
INSERT INTO food_ingredient_tbl (
     food_id
    , ingredient_id
    , capacity
    , unit
)
VALUES (
    #{foodId}
   , #{ingredientId}
   , #{capacity}
   , #{unit}
)

mapper.update:
UPDATE food_ingredient_tbl SET
    food_id = #{foodId}
    , ingredient_id = #{ingredientId}
    , capacity = #{capacity}
    , unit = #{unit}
WHERE id = #{id}
