mapper.update:
UPDATE ingredient_tbl SET
    name = #{name}
    , ingredient_category_id = #{ingredientCategoryId}
WHERE ID = #{id}

mapper.select:
SELECT it.id , it.name , it.ingredient_category_id , ict.name AS ingredient_category_name FROM ingredient_tbl AS it INNER JOIN ingredient_category_tbl AS ict ON it.ingredient_category_id = ict.id
1=1
AND it.name LIKE CONCAT('%', #{name}, '%')
AND it.ingredient_category_id = #{ingredientCategoryId}
ORDER BY
${item.property} ${item.direction}
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}
