mapper.insert:
INSERT INTO member_tbl (
    username
    , password
    , name
    , nick_name
    , phone_number
    , email
    , role
) VALUES (
    #{username}
    , #{password}
    , #{name}
    , #{nickName}
    , #{phoneNumber}
    , #{email}
    , #{role}
)

mapper.update:
UPDATE member_tbl SET
    id = #{id}

    , username = #{username}


    , name = #{name}


    , nick_name = #{nickName}


    , phone_number = #{phoneNumber}


    , email = #{email}


    , role = #{role}

WHERE id = #{id}

UPDATE member_tbl SET
    password = #{password}
WHERE id = #{id}

mapper.select:
SELECT id , username , nick_name , role FROM member_tbl
1=1
AND username LIKE CONCAT('%', #{username}, '%')
AND nick_name LIKE CONCAT('%', #{nickName}, '%')
AND role = #{role}
ORDER BY
${item.property} ${item.direction}
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}
